INSERT INTO `produtos` (`PRO_Codigo`, `PRO_Codigo_Categoria`, `PRO_Nome`, `PRO_Descricao_Curta`, `PRO_Descricao_Completa`, `PRO_Grama1`, `PRO_Caixa1`, `PRO_Grama2`, `PRO_Caixa2`, `PRO_Grama3`, `PRO_Caixa3`, `PRO_Grama4`, `PRO_Caixa4`, `PRO_Grama5`, `PRO_Caixa5`, `PRO_Grama6`, `PRO_Caixa6`, `PRO_Grama7`, `PRO_Caixa7`) VALUES
(47, 30, 'Tradicional', 'Limpeza e renovação celular!', 'Com ação renovadora intensiva, pode ser usado no corpo todo. Renova, remove as impurezas e prepara a pele para tratamentos complementares como hidratação, banho de lua e depilação. Indicado para todos os tipos de pele!', '150G', '24', '280G', '24', '500G', '12', '900G', '12', NULL, NULL, NULL, NULL, NULL, NULL),
(49, 30, 'Com Argila Verde', 'Proporciona todos os benefícios da argila para sua pele!', 'A argila é rica em sais minerais como ferro, silício e magnésio. Quando usado em médio/longo prazo, desintoxica a pele, aumenta a defesa natural e auxilia na renovação da células. Aliada ao nosso esfoliante, o efeito é limpeza, tratamento e hidratação. Indicado para peles sensíveis!', '90G', '24', '280G', '24', '500G', '12', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(50, 31, 'Tradicional', 'Esfoliação média, com Dolomita e Pedra Pomes!', 'O esfoliante tradicional Pelo & Pele auxilia na remoção das células mortas e impurezas. Evita rachaduras e maus cheiros. Sua fórmula ajuda a remover a calosidade dos pés, deixando-os com um toque macio.', '90G', '24', '280G', '24', '500G', '12', '900G', '12', NULL, NULL, NULL, NULL, NULL, NULL),
(51, 31, 'Com Argila Verde', 'Proporciona todos os benefícios da argila para sua pele!', 'Rica em propriedades terapêuticas, a argila elimina as toxinas e substâncias nocivas a pele, produzindo efeito antisséptico e antibacteriano. Aliada ao nosso esfoliante, o efeito é limpeza, tratamento e hidratação. Indicado para peles sensíveis!', '90G', '24', '280G', '24', '500G', '12', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(52, 30, 'Max Esfoliação', 'Esfoliação com forte abrasão, para peles mais resistentes!', 'Esfoliação intensiva, indicado para as áreas mais ressecadas, com acúmulo de células mortas, como joelhos e cotovelos. Renova a pele, remove as impurezas e tonifica, deixando o corpo macio, com toque suave e resultado luminoso de pele saudável. Indicado para peles mais resistentes!', '90G', '24', '280G', '24', '500G', '12', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(54, 30, 'Maracujá', 'Acalma, hidrata e renova a pele. (Sem Corante)', 'Rico em vitamina A e C, o maracujá possui alta concentração de ácidos graxos que hidratam, acalmam e combatem as irritações na pele! A esfoliação remove impurezas e elimina células mortas restabelecendo o equilíbrio natural da pele.', '280G', '24', '500G', '12', '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(55, 31, 'Max Esfoliação', 'Esfoliação com forte abrasão, para peles mais resistentes!', 'Remove as células mortas, hidrata a pele , suavisando calos e asperezas. Possui propriedades hidratantes e regeneradoras. Indicado para peles mais resistentes!', '90G', '24', '280G', '24', '500G', '12', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(56, 31, 'Pele Grossa e Ressecada', 'Ideal para pele muito grossa e ressecada!', 'O esfoliante pele muito grossa e ressecada Pelo & Pele ajuda na reestruturação da pele, eliminando células mortas, hidratando, amaciando e limpando profundamente!', '280G', '24', '500G', '12', '', '', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(57, 32, 'Tradicional', 'Proteção e hidratação para sua pele!', 'Ideal para proteger a pele de possíveis reações na aplicação de banhos de lua e descolorantes. Por ser um produto de hidratação intensiva, pode também ser usado em massagens e outros tratamentos estéticos.', '80G', '24', '150G', '24', '400G', '12', '800G', '12', NULL, NULL, NULL, NULL, NULL, NULL),
(58, 32, 'Pele Morena', 'Proteção e hidratação com os benefícios do óleo de amêndoas!', 'Ideal para proteger a pele de possíveis reações na aplicação de banhos de lua e descolorantes. Por ser um produto de hidratação intensiva, pode também ser usado em massagens e outros tratamentos estéticos. O óleo de amêndoas é um excelente hidratante, melhora a elasticidade da pele e ajuda também a reduzir a inflamação da pele.', '80G', '24', '150G', '24', '400G', '12', '800G', '12', NULL, NULL, NULL, NULL, NULL, NULL),
(59, 32, 'Chocolate', 'Hidratação intensiva e pele reluzente!', 'Ideal para proteger a pele de possíveis reações na aplicação de banhos de lua e descolorantes. Por ser um produto de hidratação intensiva, pode também ser usado em massagens e outros tratamentos estéticos. O chocolate é rico em antioxidantes e possui ação umectante sendo eficaz para revitalizar a pele!', '80G', '24', '150G', '24', '400G', '12', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(60, 32, 'Com Óleo de Coco', 'Hidratação com benefícios do Óleo de coco!', 'Ideal para proteger a pele de possíveis reações na aplicação de banhos de lua e descolorantes. O óleo de coco mantém a pele macia sem obstruir os poros e sem deixá-la oleosa. Com efeito anti-inflamatório, ele hidrata, acalma, e previne os sinais de envelhecimento!', '80G', '24', '150G', '24', '400G', '12', '', '', NULL, NULL, NULL, NULL, NULL, NULL),
(61, 33, 'Ceras Depilatórias Naturais', '100% natural e hidrossolúvel, enriquecidas com vitamina C.', 'Cera depilatória Natural - A base de limão e mel, livre de essências ou corantes, a CERA NATURAL é indicada para todos os tipos de pele! \r\n\r\nCera depilatória Mel - Especial para pelos mais finos, alta eficiência, maior rendimento e ação hidratante, indicada para todos os tipos de pele!\r\n\r\nCera depilatória Maracujá - Rico em vitaminas A e C, o maracujá hidrata a pele, age contra infecções e ajuda na cicatrização!\r\n\r\nCera depilatória Frutas Vermelhas - Levemente perfumada, a Cera Frutas Vermelhas permite uma depilação rápida, eficiente e pode ser aplicada em todo o corpo: pernas, braços, buço, virilha e axilas. Indicado para todos os tipos de pele!', 'Limão 350G', '24', 'Limão 680G', '12', 'Mel 350G', '24', 'Mel 650G', '18', 'Maracujá 350G', '24', 'Maracujá 680G', '18', 'Frutas V. 350G', '24'),
(62, 33, 'Perlon para depilação', 'Descartável, prático e higiênico!', 'É ideal para uso em depilações com cera quente natural Pelo & Pele ou roll-on. É hipoalergênico, extremamente higiênico e resistente.', '100 Folhas', '24', '50 Folhas ', '12', '', '', '', '', '', '', '', '', '', ''),
(63, 33, 'Higienizador e Removedor', 'Pré e Pós Depilação!', 'Higienizador Pré Depilação: Prepara a pele para a depilação, removendo a oleosidade excessiva e as impurezas, facilitando a extração dos pelos.\r\n\r\nRemovedor de Cera: À base de própolis, remove os resíduos pós depilatórios, ajudando na cicatrização, evitando pelos encravados e diminuindo a hipersensibilidade.', 'Higienizador 100ML', '24', 'Removedor 100ML', '24', '', '', '', '', '', '', '', '', '', ''),
(64, 33, 'Talco pré depilação', 'Cera somente nos pelos, pele protegida!', 'Retira a umidade e evita o contato da Cera com a pele, aderindo somente nos pelos, o que faz a depilação ser mais eficiente com menos dor.', '200G', '24', '750G', '12', '', '', '', '', '', '', '', '', '', ''),
(65, 34, 'Kit Especial', 'Tratamento completo, trazendo 9 itens!', 'Descolore sem incômodos e hidrata a pele, deixando os pelos do corpo dourados. Contém Parafina com óleo de coco e máscara nutritiva de Argila Branca, que ajuda a minimizar as agressões causadas pelo processo de descoloração, auxiliando na maciez e hidratação da pele.', 'Sacola', '24', 'Caixa', '24', '', '', '', '', '', '', '', '', '', ''),
(66, 34, 'Kit Básico', 'Descoloração rápida e de fácil aplicação!', 'Descolore sem incômodos e hidrata a pele, deixando os pelos do corpo dourados. Contém Parafina com óleo de coco que ajuda a minimizar as agressões causadas pelo processo de descoloração.', 'Sacola', '24', 'Transparente', '24', '', '', '', '', '', '', '', '', '', ''),
(67, 34, 'Kit essencial', 'Pele limpinha e Pelos dourados!', 'Descolore sem incômodos e hidrata a pele, deixando os pelos do corpo dourados. Contém Parafina com óleo de coco que ajuda a minimizar as agressões causadas pelo processo de descoloração auxiliando na maciez e hidratação da pele.', 'Sacola', '12', 'Caixa', '12', '', '', '', '', '', '', '', '', '', ''),
(68, 34, 'Kit ervas', 'Com ativos naturais que nutrem e hidratam a pele!', 'Além dos benefícios de um banho de lua, o Kit ERVAS possui também ativos naturais e medicinais que dão um tratamento especial para a pele. Com propriedades energizantes, nutritivas e hidratantes, ajuda também no combate às estrias e envelhecimento precoce!', 'Caixa', '12', '', '', '', '', '', '', '', '', '', '', '', ''),
(69, 35, 'Tratamento Facial', 'Linha de prevenção e tratamento facial!', 'Peeling facial com Argila Verde: é um tratamento estético, sua ação estimula a regeneração celular, evita cravos, espinhas, rugas e combate o envelhecimento precoce.\r\n\r\nCreme Cravos e Espinhas: Com fórmula suave a base de própolis e argila verde, o creme secativo cravos e espinhas oferece hidratação e cicatrização das espinhas. Não irrita nem altera o PH natural da pele!\r\n\r\nSabonete em Pó: À base bicarbonato de sódio e amido, protege contra irritações e manchas superficiais da pele. A fórmula proporciona uma limpeza profunda, que elimina as impurezas sem remover a proteção natural da pele, podendo ser usado diariamente para a higiene e cuidados da pele.', 'Peeling 90G', '24', 'C. Secativo 150G', '24', 'Sabonete 80G', '24', '', '', '', '', '', '', '', ''),
(70, 35, 'Amolecedor de Cutículas', 'Amolecedor com PRÓPOLIS, prevenção e proteção!', 'À base de própolis, protege contra possíveis bactérias e inflamações, ajuda na cicatrização, hidrata e amolece a cutícula auxiliando sua remoção.', '100G', '24', '', '', '', '', '', '', '', '', '', '', '', ''),
(73, 35, 'Ação contra Calos e Rachaduras', 'Pele lisinha, sem calos ou rachaduras, eficácia garantida!', 'Cicatrizante para Rachaduras: De volta a liberdade! Agora seus pés estarão livres das incômodas rachaduras no calcanhar. O uso diário deste creme, vai proporcionar alívio imediato das dores e cicatrização progressiva.\r\n\r\nLixa Liquida: Um produto revolucionário, sem ácidos, à base de mel e própolis, hidrata e amacia o pés sem a necessidade de abrasivos, o uso diário da Lixa líquida serve também como ação preventiva contra pele grossa, calos e rachaduras!', 'Cicatrizante 90G', '24', 'Lixa Liquida 50ML', '24', '', '', '', '', '', '', '', '', '', ''),
(74, 35, 'Kit Saúde dos Pés', 'O cuidado especial que seus pés merecem!', 'Um verdadeiro SPA para os pés, pele fina, limpa, hidratada e livre de rachaduras. O cuidado especial que seus pés merecem!', '350G ', '24', '', '', '', '', '', '', '', '', '', '', '', ''),
(77, 36, 'Esfoliantes Especiais', 'Esfoliantes diferenciados, com abrasivos minerais!', 'Esfoliante Canforado: Alivia o cansaço e refresca os pés, deixando-os renovados e macios. Remove a camada mais superficial e seca da pele, proporcionando uma sensação refrescante e revigorante! Indicado para todos os tipos de pele! \r\n\r\nEsfoliante Efervescente: O processo de descamação natural da pele, a deixa irregular e opaca. Com uma fórmula inovadora, o esfoliante efervesecente repara essa deficiência e penetra nos poros limpando profundamente, facilitando a hidratação e o bronzeamento. Indicado para todos os tipos de pele!', 'Esfoliante Canforado 280G', '24', 'Esfoliante Efervescente 280G', '24', '', '', '', '', '', '', '', '', '', ''),
(78, 36, 'Kit Axila e Virilha', 'Hidrata e devolve progressivamente a cor natural da pele!', 'Por ser uma zona de atrito, axilas e virilhas possuem tendência natural a escurecer. Alterações hormonais, exposição ao sol e uso contínuo de produtos como desodorantes, cremes diversos e depilação com lâmina, proporcionam o surgimento de manchas escuras. O uso frequente desse kit, irá devolver progressivamente a cor natural da pele!', '300G', '24', '', '', '', '', '', '', '', '', '', '', '', ''),
(81, 36, 'Henna p/ Sombrancelha', 'Kit Henna Profissional com ativos naturais!', 'Cria sombrancelhas profissionais com aspecto natural, sua fórmula possibilita o preenchimento completo das falhas, resultando em desenhos definidos , deixando o visual mais atraente.', 'Castanho Claro', '24', 'Castanho Médio', '24', 'Castanho Escuro', '24', 'Chocolate', '24', 'Loiro Escuro', '24', 'Marrom', '24', 'Preto', '24'),
(82, 37, 'Sais de Banho 100G', 'Limpa, trata e perfuma a pele!', 'Os sais de banho deixam o banho muito mais delicioso. Eles ajudam a relaxar, exalam aromas e massageiam a pele. Além disso, fazem uma esfoliação na camada superficial da pele e permitem que qualquer produto que seja aplicado depois tenha maior absorção.', 'Afrodisíaco', '60', 'Exótico', '60', 'Sensual', '60', '', '', '', '', '', '', '', ''),
(83, 37, 'Talco Extre Fino', 'Talco neutro, indicado para uso geral!', 'Um produto muito versátil, pode ser usado em várias ocasiões: após o corte de cabelo, para massagem, para evitar suor nos pés, para tirar oleosidade dos cabelos, para acalmar a pele, entre outras...', 'Sache 750G', '12', '', '', '', '', '', '', '', '', '', '', '', ''),
(85, 37, 'Argilas', '100% pura, para tratamentos medicinais e estésticos!', 'Argila Branca: Possui propriedades cicatrizantes, purificantes, adstringentes, antisséptica, entre outras... Clareia manchas de senilidade, age como desintoxicante corporal e reduz a oleosidade da pele e rugas de expressão!\r\n\r\nArgila Verde: 100% natural e rica em minerais como ferro, potássio, cálcio, magnésio e outros hidróxidos coloidais. Quando usada a médio/longo prazo, desintoxica a pele, aumenta a defesa natural e contribui com a renovação celular. Reduz a oleosidade do couro cabeludo e cabelo, regula a queratinização, hidrata, combate o envelhecimento precoce, previne rugas, cravos e espinhas!', 'Argila Branca 500G', '24', 'Argila Branca 80G', '84', 'Argila Verde 100G', '96', 'Argila Verde 500G', '24', '', '', '', '', '', ''),
(86, 39, 'Máscaras Faciais', 'Máscaras com efeito detox para a pele!', 'O Carvão Ativado remove impurezas, toxinas e até resquícios de maquiagem, o que proporciona para a pele, luminosidade, suavidade, maciez, proteção e uniformidade, auxilia na remoção de células mortas, controla a oleosidade e reduz o tamanho dos poros!\r\n\r\nArgila Verde tem um duplo poder: funciona, em simultâneo, como máscara e esfoliante. Possui propriedades de absorção de impurezas e de efeito detox para a pele. A Argila Verde desintoxica, reduz a oleosidade, combate o envelhecimento precoce, desobstrui os poros, renova as células, remineraliza e revitaliza a cútis!\r\n\r\nArgila Branca possui propriedades: Hidratante, Clareadora, Cicatrizante, Purificante, entre outras. Controla a oleosidade prevenindo rugas e espinhas! Este mineral é composto por óxido de alumínio, óxido de silício e sais minerais que suavizam e acalmam a pele, deixando-a visivelmente transformada, mais bonita e radiante.', '120G', '24', '120G', '24', '120G', '24', '', '', '', '', '', '', '', ''),
(87, 39, 'Óleos Corporais', 'Acalma, hidrata e renova a pele. (Sem Corante)', 'Com aroma Afrodisíaco, o óleo corporal hidrata, tonifica, combate o envelhecimento precoce e ressecamento, mantendo a elasticidade da pele.\r\n\r\nCom aroma Sensual, o óleo corporal hidrata, tonifica, combate o envelhecimento precoce e ressecamento, mantendo a elasticidade da pele.\r\n\r\nCom aroma Exótico, o óleo corporal hidrata, tonifica, combate o envelhecimento precoce e ressecamento, mantendo a elasticidade da pele.', '100ML', '24', '100ML', '24', '100ML', '24', '', '', '', '', '', '', '', ''),
(88, 39, 'Sais de banho', 'Proporciona todos os benefícios da argila para sua pele!', 'Com aroma Afrodisíaco, proporciona uma sensação de relaxamento, limpeza e bem-estar. Pode ser usado na banheira, no chuveiro e como escalda-pés.\r\n\r\nCom aroma Sensual, proporciona uma sensação de relaxamento, limpeza e bem-estar. Pode ser usado na banheira, no chuveiro e como escalda-pés.\r\n\r\nCom aroma Exótico, proporciona uma sensação de relaxamento, limpeza e bem-estar. Pode ser usado na banheira, no chuveiro e como escalda-pés.', '150G', '24', '150G', '24', '150G', '24', '', '', '', '', '', '', '', ''),
(89, 38, 'Argilas em frasco', 'Argila 100% pura em embalagem sustentável!', 'Argila Branca: Possui propriedades cicatrizantes, purificantes, adstringentes, antisséptica, entre outras... Clareia manchas de senilidade, age como desintoxicante corporal e reduz a oleosidade da pele e rugas de expressão!\r\n\r\nArgila Verde: 100% natural e rica em minerais como ferro, potássio, cálcio, magnésio e outros hidróxidos coloidais. Quando usada a médio/longo prazo, desintoxica a pele, aumenta a defesa natural e contribui com a renovação celular. Reduz a oleosidade do couro cabeludo e cabelo, regula a queratinização, hidrata, combate o envelhecimento precoce, previne rugas, cravos e espinhas!', 'Argila Branca 220G', '06', 'Argila Verde 300G', '06', '', '', '', '', '', '', '', '', '', ''),
(90, 38, 'Parafina Ativadora', 'Pele sedosa e radiante!', 'Além de proteger a pele de possíveis reações alérgicas na aplicação de banhos de lua e descolorantes, a Parafina Ativadora destaca e prolonga o bronzeado, hidrata evitando o ressecamento da pele!', '400G', '12', '80G', '24', '', '', '', '', '', '', '', '', '', ''),
(91, 38, 'Ceras depilatórias', 'Fórmula especial que remove os pelos com menos dor!', 'Mel e Limão: A Cera Mel e Limão promove uma depilação fácil e menos dolorida, possui componentes naturais como o Mel, rico em nutrientes que ajudam na renovação celular deixando a pele macia e hidratada. Indicado para todo tipo de pele.\r\n\r\nCera Pernas: A Cera Pernas foi especialmente formulada para pelos resistentes, possui maior quantidade de mel, que além de propriedades cicatrizantes, envolve melhor os pelos grossos, arrancando-os por inteiro. Indicado para todo tipo de pele.\r\n\r\nMel e Maracujá: A Cera Mel e Maracujá proporciona uma depilação mais suave e delicada, o maracujá possui uma substância chamada passiflorina, que amolece os pelos, ameniza dores e inflamações. Indicado para todo tipo de pele.', 'Mel e Limão', '24', 'Pemas', '24', 'Mel e Maracujá', '24', '', '', '', '', '', '', '', ''),
(92, 37, 'Descolorantes 50G', 'Não engrossa nem influencia no crescimento dos pelos!', 'O Pó descolorante Pelo & Pele é exclusivo para uso corporal, capaz de descolorir pelos mais grossos, sua fórmula contém ativos que limpam a pele e descolore os pelos com rapidez!', 'Argila Rosa', '192', 'Argila Verde', '192', 'Camomila', '192', 'Mel', '192', 'Menta', '192', '', '', '', ''),
(93, 36, 'Argilas Orgânicas', 'Argila 100% pura em embalagem sustentável!', 'Argila Branca: Possui propriedades cicatrizantes, purificantes, adstringentes, antisséptica, entre outras... Clareia manchas de senilidade, age como desintoxicante corporal e reduz a oleosidade da pele e rugas de expressão!\r\n\r\nArgila Verde: 100% natural e rica em minerais como ferro, potássio, cálcio, magnésio e outros hidróxidos coloidais. Quando usada a médio/longo prazo, desintoxica a pele, aumenta a defesa natural e contribui com a renovação celular. Reduz a oleosidade do couro cabeludo e cabelo, regula a queratinização, hidrata, combate o envelhecimento precoce, previne rugas, cravos e espinhas!', 'Argila Branca 220G', '24', 'Argila Verde 300G', '24', '', '', '', '', '', '', '', '', '', ''),
(94, 39, 'Hidratantes', 'Hidratação intensiva com ativos naturais e Glicerina!', 'Desenvolvido para atender as necessidades da pele fragilizada. Com vitaminas e uma fórmula exclusiva com alto poder de hidratação, ajuda a reparar e repor os nutrientes perdidos durante as agressões externas (frio, calor, sol, vento e poluição), evitando incômodos decorrentes do ressecamento!', '100G Afrodisíaco', '24', '100G Exótico', '24', '100G Sensual', '24', '', '', '', '', '', '', '', '');
